import React from "react";
import GoogleSignin from "../img/btn_google_signin_dark_pressed_web.png";
import { auth } from "../firebase";
import { GoogleAuthProvider, signInWithRedirect } from "firebase/auth";

const Welcome = () => {
  const googleSignIn = () => {
    const provider = new GoogleAuthProvider();
    signInWithRedirect(auth, provider);
  };

  return (
    <main className="welcome">
      <h2>Welcome to my Chat App.</h2>
      <img src="/logo512.png" alt="ReactJs logo" width={50} height={50} />
      <p>Sign in with Google to chat with your friends.</p>
      <button className="sign-in">
        <img
          onClick={googleSignIn}
          src={GoogleSignin}
          alt="sign in with google"
          type="button"
        />
      </button>

    <button onClick={googleSignIn} data-text="Awesome" class="button">
    <span class="actual-text">&nbsp;sign in with google&nbsp;</span>
    <span class="hover-text" aria-hidden="true">&nbsp;sign in with google&nbsp;</span>
    </button>
    </main>
  );
};

export default Welcome;
